import React, { useCallback, useState } from "react";
import Message from "./Message";
import Markdown from "react-markdown";

export default function UseCallback() {
  const [num, setNum] = useState(0);
  const [toggle, setToggle] = useState(false);
  console.log("call back render");
  const handleIncrementMessage = useCallback(() => {
    setNum(num + 1);
  }, []);
  const markdown1 = `
  const [num, setNum] = useState(0);

  const [toggle, setToggle] = useState(false);

  console.log("call back render");

  const handleIncrementMessage = useCallback(() => {

    setNum(num + 1);

  }, []);

  `;
  const markdown2 = `
  return (

    <div>

      {toggle ? "On" : "False"}

      <button

        className="btn btn-primary ms-3"

        onClick={() => {

          setToggle(!toggle);

        }}

      >
        Toggle

      </button>

      <p className="py-2 px-3 rounded markdownBgColor">

        <Markdown>{markdown1}</Markdown>

      </p>

      <h1>Initial Value : {num}</h1>
      <button
        className="btn btn-primary"
        onClick={() => {
          setNum((num) => num + 1);
        }}
      >
        Increment
      </button>
      <Message
        initialValueOfMsg={num}
        onHangleIncrement={handleIncrementMessage}
      />
    </div>
  );

  `;
  return (
    <div>
      {toggle ? "On" : "False"}
      <button
        className="btn btn-primary ms-3"
        onClick={() => {
          setToggle(!toggle);
        }}
      >
        Toggle
      </button>
      <p className="py-2 px-3 rounded markdownBgColor">
        <Markdown>{markdown1}</Markdown>
      </p>
      <h1>Initial Value : {num}</h1>
      <button
        className="btn btn-primary"
        onClick={() => {
          setNum((num) => num + 1);
        }}
      >
        Increment
      </button>
      <Message
        initialValueOfMsg={num}
        onHangleIncrement={handleIncrementMessage}
      />
    </div>
  );
}
